import { cva, type VariantProps } from "class-variance-authority";
import { html, LitElement, nothing } from "lit";
import { customElement, property } from "lit/decorators.js";
import { TW } from "@/lib/tailwindMixin";
import { cn } from "@/lib/utils";

export const badgeVariants = cva(
  "inline-flex items-center rounded-full border px-2.5 py-0.5 text-xs font-semibold transition-colors focus:outline-none focus:ring-2 focus:ring-ring focus:ring-offset-2",
  {
    variants: {
      variant: {
        default:
          "border-transparent bg-primary text-primary-foreground hover:bg-primary/80",
        secondary:
          "border-transparent bg-secondary text-secondary-foreground hover:bg-secondary/80",
        destructive:
          "border-transparent bg-destructive text-destructive-foreground hover:bg-destructive/80",
        outline: "text-foreground",
      },
    },
    defaultVariants: {
      variant: "default",
    },
  },
);

export type BadgeVariants = VariantProps<typeof badgeVariants>;

@customElement("ui-badge")
export class Badge extends TW(LitElement) {
  @property({ type: String }) variant: BadgeVariants["variant"] = "default";

  @property({ type: String, attribute: "aria-label" }) accessor ariaLabel:
    | string
    | null = null;

  private get badgeClasses() {
    return badgeVariants({ variant: this.variant });
  }

  override render() {
    return html`
      <span
        class=${cn(this.badgeClasses, this.className)}
        role="status"
        aria-label=${this.ariaLabel || nothing}
      >
        <slot></slot>
      </span>
    `;
  }
}

declare global {
  interface HTMLElementTagNameMap {
    "ui-badge": Badge;
  }
}

import "./badge";
import type { Meta, StoryObj } from "@storybook/web-components-vite";
import { html } from "lit";
import type { BadgeVariants } from "./badge";

type BadgeArgs = {
  variant?: BadgeVariants["variant"];
  ariaLabel: string | null;
  children?: string;
};

/**
 * Displays a badge or a component that looks like a badge.
 */
const meta: Meta<BadgeArgs> = {
  title: "ui/Badge",
  component: "ui-badge",
  tags: ["autodocs"],
  argTypes: {
    variant: {
      control: "select",
      options: ["default", "secondary", "destructive", "outline"],
    },
    ariaLabel: {
      control: "text",
    },
    children: {
      control: "text",
    },
  },
  args: {
    variant: "default",
    children: "Badge",
  },
  parameters: {
    layout: "centered",
  },
  render: (args) =>
    html`<ui-badge .variant=${args.variant} .ariaLabel=${args.ariaLabel}>
      ${args.children}
    </ui-badge>`,
};

export default meta;

type Story = StoryObj<BadgeArgs>;

/**
 * The default form of the badge.
 */
export const Default: Story = {};

/**
 * Use the `secondary` badge to call for less urgent information, blending
 * into the interface while still signaling minor updates or statuses.
 */
export const Secondary: Story = {
  args: {
    variant: "secondary",
  },
};

/**
 * Use the `destructive` badge to  indicate errors, alerts, or the need for
 * immediate attention.
 */
export const Destructive: Story = {
  args: {
    variant: "destructive",
  },
};

/**
 * Use the `outline` badge for overlaying without obscuring interface details,
 * emphasizing clarity and subtlety..
 */
export const Outline: Story = {
  args: {
    variant: "outline",
  },
};
