import {
  arrow,
  autoUpdate,
  computePosition,
  flip,
  offset,
  type Placement,
  type Strategy,
  shift,
} from "@floating-ui/dom";
import { css, html, LitElement, type PropertyValues } from "lit";
import { customElement, property, query, state } from "lit/decorators.js";
import { TW } from "@/registry/lib/tailwindMixin";
import { cn } from "@/registry/lib/utils";

@customElement("ui-popover")
export class Popover extends TW(LitElement) {
  @property() placement: Placement = "top";
  @property({ type: Number }) distance = 0;
  @property({ type: Number }) skidding = 0;
  @property({ type: Boolean }) flip = false;
  @property({ type: Boolean }) shift = false;
  @property({ type: Boolean }) arrow = false;
  @property({ type: Number }) arrowPadding = 0;
  @property() strategy: Strategy = "absolute";
  @property({ type: Boolean, reflect: true }) active = false;
  @property({ attribute: false }) anchor?: Element | string;

  @query('[part="popup"]') popup!: HTMLElement;
  @query('[part="arrow"]') arrowElement?: HTMLElement;

  @state() private currentPlacement: Placement = "top";

  private cleanup?: () => void;
  private anchorEl?: Element | null;

  static styles = css`
    :host {
      display: contents;
    }
  `;

  override async connectedCallback() {
    super.connectedCallback();
    await this.updateComplete;
    this.handleAnchorChange();
  }

  override disconnectedCallback() {
    super.disconnectedCallback();
    this.stop();
  }

  override async updated(changedProperties: PropertyValues) {
    super.updated(changedProperties);

    if (changedProperties.has("anchor")) {
      await this.handleAnchorChange();
    }

    if (changedProperties.has("active")) {
      if (this.active) {
        this.start();
      } else {
        this.stop();
      }
    }

    if (this.active) {
      await this.updateComplete;
      this.reposition();
    }
  }

  private async handleAnchorChange() {
    await this.stop();

    if (this.anchor && typeof this.anchor === "string") {
      const root = this.getRootNode() as Document | ShadowRoot;
      this.anchorEl = root.getElementById(this.anchor);
    } else if (this.anchor instanceof Element) {
      this.anchorEl = this.anchor;
    } else if (
      this.anchor &&
      typeof this.anchor === "object" &&
      "getBoundingClientRect" in this.anchor
    ) {
      this.anchorEl = this.anchor;
    } else {
      const slot = this.renderRoot.querySelector<HTMLSlotElement>(
        'slot[name="anchor"]',
      );
      this.anchorEl = slot?.assignedElements({ flatten: true })[0] || null;
    }

    if (this.anchorEl instanceof HTMLSlotElement) {
      this.anchorEl = this.anchorEl.assignedElements({
        flatten: true,
      })[0] as HTMLElement;
    }

    if (this.anchorEl && this.active) {
      this.start();
    }
  }

  private start() {
    if (!this.anchorEl || !this.popup) return;

    this.cleanup = autoUpdate(this.anchorEl, this.popup, () => {
      this.reposition();
    });
  }

  private async stop(): Promise<void> {
    return new Promise((resolve) => {
      if (this.cleanup) {
        this.cleanup();
        this.cleanup = undefined;
        this.removeAttribute("data-current-placement");
        requestAnimationFrame(() => resolve());
      } else {
        resolve();
      }
    });
  }

  show() {
    this.active = true;
  }

  hide() {
    this.active = false;
  }

  toggle() {
    this.active = !this.active;
  }

  reposition() {
    if (!this.active || !this.anchorEl || !this.popup) return;

    const middleware = [
      offset({ mainAxis: this.distance, crossAxis: this.skidding }),
    ];

    if (this.flip) {
      middleware.push(flip());
    }

    if (this.shift) {
      middleware.push(shift({ padding: 5 }));
    }

    if (this.arrow && this.arrowElement) {
      middleware.push(
        arrow({ element: this.arrowElement, padding: this.arrowPadding }),
      );
    }

    computePosition(this.anchorEl, this.popup, {
      placement: this.placement,
      middleware,
      strategy: this.strategy,
    }).then(({ x, y, placement, middlewareData }) => {
      Object.assign(this.popup.style, {
        position: this.strategy,
        left: `${x}px`,
        top: `${y}px`,
      });

      this.currentPlacement = placement;
      this.setAttribute("data-current-placement", placement);

      if (this.arrow && this.arrowElement && middlewareData.arrow) {
        const { x: arrowX, y: arrowY } = middlewareData.arrow;

        const staticSide = {
          top: "bottom",
          right: "left",
          bottom: "top",
          left: "right",
        }[placement.split("-")[0]] as string;

        Object.assign(this.arrowElement.style, {
          left: arrowX != null ? `${arrowX}px` : "",
          top: arrowY != null ? `${arrowY}px` : "",
          [staticSide]: "-4px",
        });
      }

      this.dispatchEvent(
        new CustomEvent("popover-reposition", {
          detail: { placement, x, y },
          bubbles: true,
          composed: true,
        }),
      );
    });
  }

  override render() {
    return html`
      <slot name="anchor" @slotchange=${this.handleAnchorChange}></slot>
      <div
        class=${cn("w-max z-50", this.active ? "" : "hidden", this.className)}
        part="popup"
        data-placement=${this.currentPlacement}
      >
        <slot></slot>
        ${
          this.arrow
            ? html`<div
              class="absolute w-2 h-2 rotate-45 bg-inherit -z-10"
              part="arrow"
            ></div>`
            : ""
        }
      </div>
    `;
  }
}

declare global {
  interface HTMLElementTagNameMap {
    "ui-popover": Popover;
  }

  interface HTMLElementEventMap {
    "popover-reposition": CustomEvent<{
      placement: Placement;
      x: number;
      y: number;
    }>;
  }
}

import type { Placement, Strategy } from "@floating-ui/dom";
import type { Meta, StoryObj } from "@storybook/web-components-vite";
import { html } from "lit";
import "../button/button.js";
import "./popover";
import type { Popover } from "./popover.js";

type PopoverArgs = {
  placement: Placement;
  distance: number;
  skidding: number;
  flip: boolean;
  shift: boolean;
  arrow: boolean;
  arrowPadding: number;
  strategy: Strategy;
  active: boolean;
  content?: string;
};

/**
 * Displays rich content in a portal, triggered by a button.
 */
const meta: Meta<PopoverArgs> = {
  title: "ui/Popover",
  component: "ui-popover",
  tags: ["autodocs"],
  argTypes: {
    placement: {
      control: "select",
      options: [
        "top",
        "top-start",
        "top-end",
        "right",
        "right-start",
        "right-end",
        "bottom",
        "bottom-start",
        "bottom-end",
        "left",
        "left-start",
        "left-end",
      ],
    },
    distance: {
      control: { type: "number", min: 0, max: 50 },
    },
    skidding: {
      control: { type: "number", min: -50, max: 50 },
    },
    flip: {
      control: "boolean",
    },
    shift: {
      control: "boolean",
    },
    arrow: {
      control: "boolean",
    },
    arrowPadding: {
      control: { type: "number", min: 0, max: 20 },
    },
    strategy: {
      control: "select",
      options: ["absolute", "fixed"],
    },
    active: {
      control: "boolean",
    },
    content: {
      control: "text",
    },
  },
  parameters: {
    layout: "centered",
  },
  args: {
    placement: "top",
    distance: 8,
    skidding: 0,
    flip: true,
    shift: true,
    arrow: false,
    arrowPadding: 0,
    strategy: "absolute",
    active: false,
    content: "Place content for the popover here.",
  },
  render: (args) => {
    const handleToggle = (e: Event) => {
      const button = e.target as HTMLElement;
      const popover = button.closest("ui-popover") as Popover;
      popover?.toggle();
    };

    return html`
      <div class="flex min-h-64 items-center justify-center">
        <ui-popover
          .placement=${args.placement}
          .distance=${args.distance}
          .skidding=${args.skidding}
          .flip=${args.flip}
          .shift=${args.shift}
          .arrow=${args.arrow}
          .arrowPadding=${args.arrowPadding}
          .strategy=${args.strategy}
          .active=${args.active}
        >
          <ui-button slot="anchor" variant="outline" @click=${handleToggle}>
            Open
          </ui-button>
          <div
            class="w-72 rounded-md border bg-popover p-4 text-popover-foreground shadow-md outline-none"
          >
            ${args.content}
          </div>
        </ui-popover>
      </div>
    `;
  },
};

export default meta;

type Story = StoryObj<PopoverArgs>;

/**
 * The default form of the popover.
 */
export const Default: Story = {};

/**
 * Popover positioned to the right.
 */
export const Right: Story = {
  args: {
    placement: "right",
    content: "Content positioned to the right.",
  },
};

/**
 * Popover positioned to the bottom.
 */
export const Bottom: Story = {
  args: {
    placement: "bottom",
    content: "Content positioned to the bottom.",
  },
};

/**
 * Demonstrates imperative control using show(), hide(), and toggle() methods.
 */
export const ImperativeControl: Story = {
  args: {
    content: "Use show() and hide() methods for explicit control.",
  },
  render: (args) => {
    const handleShow = (e: Event) => {
      const button = e.target as HTMLElement;
      const popover = button.closest("ui-popover") as Popover;
      popover?.show();
    };

    const handleHide = (e: Event) => {
      const button = e.target as HTMLElement;
      const popover = button.closest("ui-popover") as Popover;
      popover?.hide();
    };

    return html`
      <div class="flex min-h-64 items-center justify-center gap-2">
        <ui-popover
          .placement=${args.placement}
          .distance=${args.distance}
          .skidding=${args.skidding}
          .flip=${args.flip}
          .shift=${args.shift}
          .arrow=${args.arrow}
          .arrowPadding=${args.arrowPadding}
          .strategy=${args.strategy}
          .active=${args.active}
        >
          <div slot="anchor" class="flex gap-2">
            <ui-button variant="default" @click=${handleShow}>Show</ui-button>
            <ui-button variant="outline" @click=${handleHide}>Hide</ui-button>
          </div>
          <div
            class="w-72 rounded-md border bg-popover p-4 text-popover-foreground shadow-md outline-none"
          >
            ${args.content}
          </div>
        </ui-popover>
      </div>
    `;
  },
};

/**
 * Custom styling can be applied via the class attribute on the popover.
 */
export const CustomStyling: Story = {
  args: {
    content:
      "This popover has a max-width applied via the class attribute on the ui-popover element. The class is forwarded to the popup container.",
  },
  render: (args) => {
    const handleToggle = (e: Event) => {
      const button = e.target as HTMLElement;
      const popover = button.closest("ui-popover") as Popover;
      popover?.toggle();
    };

    return html`
      <div class="flex min-h-64 items-center justify-center">
        <ui-popover
          class="max-w-sm"
          .placement=${args.placement}
          .distance=${args.distance}
          .skidding=${args.skidding}
          .flip=${args.flip}
          .shift=${args.shift}
          .arrow=${args.arrow}
          .arrowPadding=${args.arrowPadding}
          .strategy=${args.strategy}
          .active=${args.active}
        >
          <ui-button slot="anchor" variant="outline" @click=${handleToggle}>
            Open with custom width
          </ui-button>
          <div
            class="rounded-md border bg-popover p-4 text-popover-foreground shadow-md outline-none"
          >
            ${args.content}
          </div>
        </ui-popover>
      </div>
    `;
  },
};
