import { cva, type VariantProps } from "class-variance-authority";
import { html, LitElement, nothing, type PropertyValues } from "lit";
import { customElement, property, state } from "lit/decorators.js";
import { TW } from "@/registry/lib/tailwindMixin";

export const toggleVariants = cva(
  "inline-flex items-center justify-center gap-2 rounded-md text-sm font-medium hover:bg-muted hover:text-muted-foreground disabled:pointer-events-none disabled:opacity-50 data-[state=on]:bg-accent data-[state=on]:text-accent-foreground [&>svg]:pointer-events-none [&>svg]:size-4 [&>svg]:shrink-0 focus-visible:border-ring focus-visible:ring-ring/50 focus-visible:ring-[3px] outline-none transition-[color,box-shadow] aria-invalid:ring-destructive/20 dark:aria-invalid:ring-destructive/40 aria-invalid:border-destructive whitespace-nowrap",
  {
    variants: {
      variant: {
        default: "bg-transparent",
        outline:
          "border border-input bg-transparent shadow-xs hover:bg-accent hover:text-accent-foreground",
      },
      size: {
        default: "h-9 px-2 min-w-9",
        sm: "h-8 px-1.5 min-w-8",
        lg: "h-10 px-2.5 min-w-10",
      },
    },
    defaultVariants: {
      variant: "default",
      size: "default",
    },
  },
);

type ToggleVariants = VariantProps<typeof toggleVariants>;

export interface ToggleChangeEvent extends CustomEvent {
  detail: { pressed: boolean };
}

export interface ToggleProperties {
  variant?: ToggleVariants["variant"];
  size?: ToggleVariants["size"];
  disabled?: boolean;
  pressed?: boolean;
  defaultPressed?: boolean;
}

@customElement("ui-toggle")
export class Toggle extends TW(LitElement) implements ToggleProperties {
  static formAssociated = true;
  private internals: ElementInternals;

  @property({ type: String }) variant: ToggleVariants["variant"] = "default";
  @property({ type: String }) size: ToggleVariants["size"] = "default";
  @property({ type: Boolean }) disabled = false;

  @property({ type: Boolean }) pressed: boolean | undefined = undefined;
  @property({ type: Boolean, attribute: "default-pressed" })
  defaultPressed = false;

  @property({ type: String, attribute: "aria-label" }) accessor ariaLabel:
    | string
    | null = null;

  @state() private _pressed = false;

  constructor() {
    super();
    this.internals = this.attachInternals();
  }

  override connectedCallback() {
    super.connectedCallback();
    if (this.pressed === undefined) {
      this._pressed = this.defaultPressed;
    }
  }

  private get isPressed(): boolean {
    return this.pressed !== undefined ? this.pressed : this._pressed;
  }

  override updated(changedProperties: PropertyValues) {
    super.updated(changedProperties);

    if (
      changedProperties.has("pressed") ||
      changedProperties.has("_pressed") ||
      changedProperties.has("disabled")
    ) {
      const state = this.isPressed ? "on" : "off";
      this.setAttribute("data-state", state);
      this.setAttribute("aria-pressed", String(this.isPressed));

      if (this.disabled) {
        this.setAttribute("data-disabled", "");
      } else {
        this.removeAttribute("data-disabled");
      }

      this.internals.setFormValue(this.isPressed ? "true" : "false");
    }
  }

  private handleClick() {
    if (this.disabled) return;

    if (this.pressed === undefined) {
      this._pressed = !this._pressed;
    }

    this.dispatchEvent(
      new CustomEvent("change", {
        detail: {
          pressed: this.pressed !== undefined ? !this.pressed : this._pressed,
        },
        bubbles: true,
        composed: true,
      }),
    );
  }

  override render() {
    return html`
      <button
        type="button"
        class=${toggleVariants({
          variant: this.variant,
          size: this.size,
        })}
        ?disabled=${this.disabled}
        aria-pressed=${this.isPressed}
        aria-label=${this.ariaLabel || nothing}
        data-state=${this.isPressed ? "on" : "off"}
        @click=${this.handleClick}
      >
        <slot></slot>
      </button>
    `;
  }
}

declare global {
  interface HTMLElementTagNameMap {
    "ui-toggle": Toggle;
  }
}

import "./toggle";
import type { Meta, StoryObj } from "@storybook/web-components-vite";
import { html } from "lit";
import { unsafeSVG } from "lit/directives/unsafe-svg.js";
import { Bold, Italic } from "lucide-static";
import type { ToggleProperties } from "./toggle";

type ToggleArgs = ToggleProperties & {
  icon?: "bold" | "italic";
  withText?: boolean;
  text?: string;
};

/**
 * A two-state button that can be either on or off.
 */
const meta: Meta<ToggleArgs> = {
  title: "ui/Toggle",
  component: "ui-toggle",
  tags: ["autodocs"],
  parameters: {
    layout: "centered",
  },
  argTypes: {
    variant: { control: "select", options: ["default", "outline"] },
    size: { control: "select", options: ["default", "sm", "lg"] },
    disabled: { control: "boolean" },
    pressed: { control: "boolean" },
    defaultPressed: { control: "boolean" },
    icon: { control: "select", options: ["bold", "italic"] },
    withText: { control: "boolean" },
    text: { control: "text" },
  },
  args: {
    variant: "default",
    size: "default",
    disabled: false,
    defaultPressed: false,
    icon: "bold",
    withText: false,
    text: "Italic",
  },
  render: (args) => {
    const iconSvg = args.icon === "bold" ? Bold : Italic;
    return html`
      <ui-toggle
        .variant=${args.variant}
        .size=${args.size}
        .disabled=${args.disabled || false}
        .pressed=${args.pressed}
        .defaultPressed=${args.defaultPressed || false}
        aria-label=${`Toggle ${args.icon}`}
      >
        ${unsafeSVG(iconSvg)} ${args.withText ? args.text : ""}
      </ui-toggle>
    `;
  },
};

export default meta;

type Story = StoryObj<ToggleArgs>;

/**
 * The default form of the toggle.
 */
export const Default: Story = {};

/**
 * Use the `outline` variant for a distinct outline, emphasizing the boundary
 * of the selection circle for clearer visibility
 */
export const Outline: Story = {
  args: {
    variant: "outline",
    icon: "italic",
  },
};

/**
 * Use the text element to add a label to the toggle.
 */
export const WithText: Story = {
  args: {
    variant: "outline",
    icon: "italic",
    withText: true,
  },
};

/**
 * Use the `sm` size for a smaller toggle, suitable for interfaces needing
 * compact elements without sacrificing usability.
 */
export const Small: Story = {
  args: {
    size: "sm",
  },
};

/**
 * Use the `lg` size for a larger toggle, offering better visibility and
 * easier interaction for users.
 */
export const Large: Story = {
  args: {
    size: "lg",
  },
};

/**
 * Add the `disabled` prop to prevent interactions with the toggle.
 */
export const Disabled: Story = {
  args: {
    disabled: true,
  },
};
